'use strict';

var shallowEqual = require('./shallowEqual');
module.exports = function shallowCompare(instance, nextProps, nextState) {
  return (
    !shallowEqual(instance.props, nextProps) ||
    !shallowEqual(instance.state, nextState)
  );
}